using System;
using System.Collections.Generic;
using System.Text;
using System.Threading.Tasks;
using Newtonsoft.Json;
using WUG.Core.Services.DataEntities;

namespace WUG.Client.Shared.Models
{
    public class TestEventProvider : IEventProvider
    {
        public async Task<List<EventInfo>> GetUpcomingEventsAsync()
        {
            await Task.Delay(2000);

            string dataBase64 = "";
            byte[] data = Convert.FromBase64String(dataBase64);

            return JsonConvert.DeserializeObject<List<EventInfo>>(Encoding.UTF8.GetString(data, 0, data.Length));
        }
    }
}
